/**
 * Create Test Image Fixture
 * Generates a minimal valid JPEG for Playwright tests
 */

const fs = require('fs');
const path = require('path');

// Minimal valid JPEG (1x1 red pixel)
// This is a base64-encoded 1x1 red JPEG image
const minimalJpegBase64 = '';

// Convert base64 to buffer
const imageBuffer = Buffer.from(minimalJpegBase64, 'base64');

// Write to fixtures directory
const outputPath = path.join(__dirname, '..', 'tests', 'fixtures', 'test-image.jpg');
fs.writeFileSync(outputPath, imageBuffer);

console.log('âœ… Test image created:', outputPath);
console.log('   Size:', imageBuffer.length, 'bytes');
console.log('   Format: JPEG (1x1 red pixel)');
